mapper.select:
SELECT LINE_CODE as "parent", LINE_CODE as "pId", REC_ID as "label", NODE_NAME as "text", 0 as leaf, 'wz' as "type", NODE_LEVEL as "sort", NODE_CODE as "nodeCode", LINE_CODE as "line" FROM SYS_REGION where 1 = 1 and NODE_LEVEL = '2'
and LINE_CODE = #{lineCode}
order by NODE_CODE asc

SELECT (SELECT REC_ID FROM SYS_REGION where REC_ID = #{realNode}) as "parent", (SELECT REC_ID FROM SYS_REGION where REC_ID = #{realNode}) as "pId", REC_ID as "label", EQUIP_NAME as "text", 0 as "leaf", 'tz' as "type", EQUIP_CODE as "nodeCode", EQUIP_NAME as "nodeName", USE_LINE_NO as "line", EQUIP_CODE as "carEquipCode", EQUIP_NAME as "carEquipName" FROM T_EQUIPMENT where 1 =1 and MAJOR_CODE='07' and APPROVAL_STATUS ='30'
and USE_LINE_NO = #{useLineNo}
order by EQUIP_CODE asc

SELECT dd1.REC_ID AS "parent", dd1.REC_ID AS "pId", dd4.REC_ID AS "label", dd4.CNAME AS "text", 0 AS "leaf", 'cc' AS "type", dd4.ENAME AS "nodeCode", dd4.CNAME AS "nodeName", dd1.USE_LINE_NO AS "line", dd1.EQUIP_CODE AS "carEquipCode", dd1.EQUIP_NAME AS "carEquipName" FROM T_BOM dd4, T_EQUIPMENT dd1
1 = 1 and dd1.MAJOR_CODE='07' and dd1.BOM_TYPE = (select ENAME FROM T_BOM where dd4.PARENT_ID = REC_ID ) and dd1.APPROVAL_STATUS='30'
AND dd1.USE_LINE_NO = #{useLineNo}
AND dd1.REC_ID = #{realNode}
ORDER BY dd4.ENAME ASC

SELECT ENAME as "ename" FROM T_BOM WHERE 1=1
AND PARENT_ID = #{carNode}
order by ENAME

