AND dd4.REC_ID = #{req.carNode} AND dd2.BOM_ENAME =#{req.carNode}
AND dd1.USE_LINE_NO = #{req.useLineNo}
AND dd1.MANUFACTURE like '%' ||#{req.manufacture}|| '%'
