mapper.update:
UPDATE T_VIDEO_ACCESS
SET
REC_REVISE_TIME = #{recReviseTime}
, REC_REVISOR = #{recRevisor}

   , TRAIN_NO = #{trainNo}


   , APPLY_REASON = #{applyReason}


   , APPLY_TYPE = #{applyType}


   , APPLIER_ID = #{applierId}


   , APPLIER_MOB_PHONE = #{applierMobPhone}


  ,  APPLY_DEPT_CODE = #{applyDeptCode}


   , APPLY_DEPT_LEADER = #{applyDeptLeader}


   , APPLY_TIME = #{applyTime}


   , VIDEO_START_TIME = #{videoStartTime}


   , VIDEO_END_TIME = #{videoEndTime}


   , EQUIP_CODE = #{equipCode}


    ,ENGINEER_ID = #{engineerId}


   , REPAIR_MANAGER_ID = #{repairManagerId}


    ,REMARK = #{remark}


   , DISPATCH_USER_ID = #{dispatchUserId}

WHERE REC_ID = #{recId}

update
T_VIDEO_ACCESS
set
DELETE_FLAG='1', REC_DELETOR=#{userId}, REC_DELETE_TIME=#{time}
WHERE REC_ID in

    #{id}

UPDATE T_VIDEO_ACCESS set
REC_STATUS=#{recStatus}

    ,DISPATCH_TIME=#{dispatchTime}


    , DISPATCH_USER_ID=#{dispatchUserId}


    , WORKER_ID=#{workerId}


    , WORK_TIME=#{workTime}


    , CLOSER_ID=#{closerId}


    , CLOSE_TIME=#{closeTime}


    , WORK_CLASS=#{workClass}

WHERE REC_ID=#{recId}

mapper.select:
SELECT REC_ID as "recId", COMPANY_CODE as "companyCode", COMPANY_NAME as "companyName", APPLY_NO as "applyNo", TRAIN_NO as "trainNo", APPLY_REASON as "applyReason", APPLY_TYPE as "applyType", APPLIER_ID as "applierId", APPLIER_MOB_PHONE as "applierMobPhone", APPLY_DEPT_CODE as "applyDeptCode", APPLY_DEPT_LEADER as "applyDeptLeader", APPLY_TIME as "applyTime", VIDEO_START_TIME as "videoStartTime", VIDEO_END_TIME as "videoEndTime", ENGINEER_ID as "engineerId", REPAIR_MANAGER_ID as "repairManagerId", DISPATCH_USER_ID as "dispatchUserId", DISPATCH_TIME as "dispatchTime", WORKER_ID as "workerId", WORK_TIME as "workTime", CLOSER_ID as "closerId", CLOSE_TIME as "closeTime", WORK_FLOW_INST_ID as "workFlowInstId", WORK_FLOW_INST_STATUS as "workFlowInstStatus", REMARK as "remark", REC_CREATOR as "recCreator", REC_CREATE_TIME as "recCreateTime", REC_REVISOR as "recRevisor", REC_REVISE_TIME as "recReviseTime", REC_DELETOR as "recDeletor", REC_DELETE_TIME as "recDeleteTime", DELETE_FLAG as "deleteFlag", ARCHIVE_FLAG as "archiveFlag", REC_STATUS as "recStatus", EXT1 as "ext1", EXT2 as "ext2", EXT3 as "ext3", EXT4 as "ext4", EXT5 as "ext5", WORK_CLASS as "workClass" FROM T_VIDEO_ACCESS
and APPLY_NO = #{applyNo}
and REC_STATUS = #{recStatus}
and TO_DATE(APPLY_TIME,'YYYY-MM-DD') >= TO_DATE(#{startApplyTime},'YYYY-MM-DD')
and TO_DATE(APPLY_TIME,'YYYY-MM-DD') <= TO_DATE(#{endApplyTime},'YYYY-MM-DD')
and DELETE_FLAG = '0'

SELECT REC_ID as "recId", COMPANY_CODE as "companyCode", COMPANY_NAME as "companyName", APPLY_NO as "applyNo", TRAIN_NO as "trainNo", APPLY_REASON as "applyReason", APPLY_TYPE as "applyType", APPLIER_ID as "applierId", APPLIER_MOB_PHONE as "applierMobPhone", APPLY_DEPT_CODE as "applyDeptCode", APPLY_DEPT_LEADER as "applyDeptLeader", APPLY_TIME as "applyTime", VIDEO_START_TIME as "videoStartTime", VIDEO_END_TIME as "videoEndTime", ENGINEER_ID as "engineerId", REPAIR_MANAGER_ID as "repairManagerId", DISPATCH_USER_ID as "dispatchUserId", DISPATCH_TIME as "dispatchTime", WORKER_ID as "workerId", WORK_TIME as "workTime", CLOSER_ID as "closerId", CLOSE_TIME as "closeTime", WORK_FLOW_INST_ID as "workFlowInstId", WORK_FLOW_INST_STATUS as "workFlowInstStatus", REMARK as "remark", REC_CREATOR as "recCreator", REC_CREATE_TIME as "recCreateTime", REC_REVISOR as "recRevisor", REC_REVISE_TIME as "recReviseTime", REC_DELETOR as "recDeletor", REC_DELETE_TIME as "recDeleteTime", DELETE_FLAG as "deleteFlag", ARCHIVE_FLAG as "archiveFlag", REC_STATUS as "recStatus", EXT1 as "ext1", EXT2 as "ext2", EXT3 as "ext3", EXT4 as "ext4", EXT5 as "ext5", WORK_CLASS as "workClass" FROM T_VIDEO_ACCESS
and APPLY_NO = #{applyNo}
and REC_STATUS = #{recStatus}
and TO_DATE(APPLY_TIME,'YYYY-MM-DD') >= TO_DATE(#{startApplyTime},'YYYY-MM-DD')
and TO_DATE(APPLY_TIME,'YYYY-MM-DD') <= TO_DATE(#{endApplyTime},'YYYY-MM-DD')
and DELETE_FLAG = '0'
