mapper.update:
UPDATE T_FAULT_TRACK_DETAIL
SET

    REC_STATUS = #{recStatus},


    TRACK_REPORTER_ID = #{trackReporterId},


    TRACK_RESULT = #{trackResult},


    TRACK_REPORT_TIME = #{trackReportTime}



        and FAULT_TRACK_NO = #{faultTrackNo}


        and FAULT_TRACK_WORK_NO = #{faultTrackWorkNo}

UPDATE T_FAULT_TRACK_DETAIL
SET
REC_STATUS = #{recStatus},
TRACK_CLOSER_ID = #{trackCloserId},
TRACK_CLOSE_TIME = #{trackCloseTime}


        and FAULT_TRACK_NO = #{faultTrackNo}


        and FAULT_TRACK_WORK_NO = #{faultTrackWorkNo}

UPDATE T_FAULT_TRACK_DETAIL SET FAULT_TRACK_WORK_NO = #{faultTrackWorkNo}, WORKER_GROUP_CODE = #{workerGroupCode}, TRACK_REPORTER_ID = #{trackReporterId}, DISPATCH_USER_ID = #{dispatchUserId}, DISPATCH_TIME = #{dispatchTime}, REC_STATUS = #recStatus#
and FAULT_TRACK_NO = #{faultTrackNo}
and FAULT_TRACK_WORK_NO = #{faultTrackWorkNo}

mapper.select:
SELECT df11.REC_ID as "recId", df11.COMPANY_CODE as "companyCode", df11.COMPANY_NAME as "companyName", df11.FAULT_TRACK_NO as "faultTrackNo", df11.FAULT_TRACK_WORK_NO as "faultTrackWorkNo", df11.DISPATCH_USER_ID as "dispatchUserId", df11.DISPATCH_TIME as "dispatchTime", df11.WORKER_GROUP_CODE as "workerGroupCode", df11.WORK_GROUP_NAME as "workGroupName", df11.TRACK_REPORTER_ID as "trackReporterId", df11.TRACK_REPORT_TIME as "trackReportTime", (Select u.USER_NAME from IPLAT60.XS_USER u where u.LOGIN_NAME =df11.TRACK_REPORTER_ID) as "trackReportName", (Select u.USER_NAME from IPLAT60.XS_USER u where u.LOGIN_NAME = df11.DISPATCH_USER_ID) as "dispatchUserName", (Select u.USER_NAME from IPLAT60.XS_USER u where u.LOGIN_NAME = df11.TRACK_CLOSER_ID) as "trackCloserName", df11.TRACK_RESULT as "trackResult", df11.TRACK_CLOSER_ID as "trackCloserId", df11.TRACK_CLOSE_TIME as "trackCloseTime", df11.DOC_ID as "docId", df11.REMARK as "remark", df11.REC_CREATOR as "recCreator", df11.REC_CREATE_TIME as "recCreateTime", df11.REC_REVISOR as "recRevisor", df11.REC_REVISE_TIME as "recReviseTime", df11.REC_DELETOR as "recDeletor", df11.REC_DELETE_TIME as "recDeleteTime", df11.DELETE_FLAG as "deleteFlag", df11.ARCHIVE_FLAG as "archiveFlag", df11.REC_STATUS as "recStatus", df1.OBJECT_CODE as "objectCode", df1.OBJECT_NAME as "objectName" FROM T_FAULT_TRACK_DETAIL df11, T_FAULT_ORDER df2,T_FAULT_TRACK df9,T_FAULT_INFO df1
and df11.FAULT_TRACK_NO = df9.FAULT_TRACK_NO and df9.FAULT_NO=df1.FAULT_NO and df9.FAULT_WORK_NO=df2.FAULT_WORK_NO and df2.FAULT_NO=df1.FAULT_NO
and df11.FAULT_TRACK_NO = #{faultTrackNo}
and df11.FAULT_TRACK_WORK_NO = #{faultTrackWorkNo}
and df1.OBJECT_CODE = #{objectCode}
and df1.OBJECT_NAME = #{objectName}
and df1.MAJOR_CODE = #{majorCode}
and df1.SYSTEM_CODE = #{systemCode}
and df1.EQUIP_TYPE_CODE = #{equipTypeCode}
and df9.REC_STATUS = #{recStatus}
order by df11.FAULT_TRACK_WORK_NO
