mapper.select:
SELECT count(*) FROM ( SELECT A.TASK_ID as taskId, A.TASK_NAME as taskName, A.PROCESS_INSTANCE_ID as processInstanceId, A.ASSIGNEE_ID as assigneeId, A.ASSIGNEE_FULLNAME as assigneeFullname, A.REC_CREATOR as recCreator, (SELECT MAX(USER_NAME) FROM iplat60.XS_USER WHERE LOGIN_NAME = A.REC_CREATOR) as recCreatorName, to_char(to_date(A.START_TIME,'YYYYMMDDHH24miss'),'YYYY-MM-DD HH24:mi:ss') as startTime, A.FORM as form, A.DURATION as duration, to_char(A.OPINION) as opinion, B.BUSINESS_KEY as businessKey, B.PROCESS_DEF_NAME as processDefName FROM iplat60.TEWPT00 A INNER JOIN iplat60.TEWPI00 B ON A.ACT_INSTANCE_ID=B.ACT_INSTANCE_ID WHERE 1=1
AND A.PROCESS_KEY LIKE CONCAT('%', #{modelName}, '%')
AND A.ASSIGNEE_ID = #{userId}
AND A.STATE = #{state}
UNION ALL SELECT EXT1 as taskId, STEP_NAME as taskName, TODO_ID as processInstanceId, USER_ID as assigneeId, (SELECT MAX(USER_NAME) FROM iplat60.XS_USER WHERE LOGIN_NAME = B.USER_ID) as assigneeFullname, LAST_STEP_USER_ID as recCreator, (SELECT MAX(USER_NAME) FROM iplat60.XS_USER WHERE LOGIN_NAME = LAST_STEP_USER_ID) as recCreatorName, to_char(to_date(TASK_RCV_TIME,'YYYYMMDDHH24miss'),'YYYY-MM-DD HH24:mi:ss') as startTime, EIP_URL as form, round(to_number((select to_date(to_char(sysdate,'yyyy-mm-dd hh24:mi:ss'),'yyyy-mm-dd hh24:mi:ss') FROM DUAL) - to_date(TASK_RCV_TIME,'yyyy-mm-dd hh24:mi:ss'))*1440) as duration, AUDIT_OPINION as opinion, EXT1 as businessKey, TITLE as processDefName FROM iplat60.STATUS_WORK_FLOW_LOG B WHERE 1=1
AND B.SYSCODE LIKE CONCAT('%', #{modelName}, '%')
AND B.USER_ID = #{userId}
AND B.TODO_STATUS = #{todoStatus}
) A ORDER BY A.startTime DESC

SELECT COUNT(*)
FROM IPLAT60.USER_MESSAGE
WHERE 1=1

    AND STATUS = #{status}


    AND RECEIVE_USER_ID = #{receiveUserId}
