mapper.select:
SELECT DISTINCT sp.*
FROM SYS_USER_ROLE sur left join SYS_ROLE_PERMISSION srp on srp.ROLE_ID=sur.ROLE_ID
left join SYS_PERMISSION sp on sp.ID=srp.PERMISSION_ID and sp.DEL_FLAG=0 and sp.STATUS=0 AND sp.IS_SHOW=1
WHERE sp.TYPE=2 AND sp.PARENT_ID IS NOT NULL and sur.USER_ID=#{personId}

    AND sp.PARENT_ID=#{catalogId}

ORDER BY sp.SORT

SELECT DISTINCT sp.*
FROM SYS_USER_ROLE sur left join SYS_ROLE_PERMISSION srp on srp.ROLE_ID=sur.ROLE_ID
left join SYS_PERMISSION sp on sp.ID=srp.PERMISSION_ID and sp.DEL_FLAG=0 and sp.STATUS=0 AND sp.IS_SHOW=1
WHERE sp.TYPE=3 AND sp.PARENT_ID IS NOT NULL and sur.USER_ID=#{personId}

    AND sp.PARENT_ID=#{menuId}

ORDER BY sp.SORT

SELECT * FROM SYS_PERMISSION WHERE TYPE=2 AND DEL_FLAG=0 AND PARENT_ID IS NOT NULL AND STATUS=0 AND IS_SHOW=1
AND PARENT_ID=#{catalogId}
ORDER BY SORT

SELECT * FROM SYS_PERMISSION WHERE TYPE=3 AND DEL_FLAG=0 AND PARENT_ID IS NOT NULL AND STATUS=0 AND IS_SHOW=1
AND PARENT_ID=#{menuId}
ORDER BY SORT

SELECT ID as id,NAME as name,PARENT_ID as parentId,PERM_CODE as permCode, TYPE as type ,URL as url,SORT as sort,COMPONENT as component, IS_SHOW as isShow,STATUS as status,ICON as icon FROM SYS_PERMISSION WHERE TYPE=1 AND DEL_FLAG=0 AND PARENT_ID IS NULL
AND ID IN (
#{menuIds.menuId}
)
ORDER BY SORT

SELECT
    ID as id,NAME as name,PARENT_ID as parentId,PERM_CODE as permCode,
    TYPE as type ,URL as url,SORT as sort,COMPONENT as component,
    IS_SHOW as isShow,STATUS as status,ICON as icon
FROM SYS_PERMISSION WHERE TYPE=2 AND DEL_FLAG=0 AND PARENT_ID IS NOT NULL

    AND PARENT_ID=#{catalogId}


    AND ID IN (

        #{menuId}

    )

ORDER BY SORT

SELECT
    ID as id,NAME as name,PARENT_ID as parentId,PERM_CODE as permCode,
    TYPE as type ,URL as url,SORT as sort,COMPONENT as component,
    IS_SHOW as isShow,STATUS as status,ICON as icon
FROM SYS_PERMISSION WHERE TYPE=3 AND DEL_FLAG=0 AND PARENT_ID IS NOT NULL

    AND PARENT_ID=#{menuId}


    AND ID IN (

        #{id}

    )

ORDER BY SORT
