mapper.update:
UPDATE T_OTHER_EQUIP_TYPE SET

    TYPE_NAME=#{typeName,jdbcType=VARCHAR},


    DETECTION_PERIOD=#{detectionPeriod,jdbcType=VARCHAR},

REC_REVISOR=#{recRevisor},
REC_REVISE_TIME=#{recReviseTime}
WHERE REC_ID = #{recId,jdbcType=VARCHAR}

UPDATE T_OTHER_EQUIP_TYPE SET
DELETE_FLAG='1', REC_DELETOR=#{userId}, REC_DELETE_TIME=#{time}
WHERE REC_ID IN (

    #{id}

)

mapper.select:
SELECT REC_ID, TYPE_CODE, TYPE_NAME, DETECTION_PERIOD, REC_CREATOR, REC_CREATE_TIME, REC_REVISOR, REC_REVISE_TIME, REC_DELETOR, REC_DELETE_TIME, DELETE_FLAG FROM T_OTHER_EQUIP_TYPE where DELETE_FLAG='0'
and TYPE_CODE=#{code}
and TYPE_NAME=#{name}
and rownum=1
