mapper.select:

FROM T_WEEKLY_PLAN WHERE 1=1 and DELETE_FLAG='0'

    and WEEK_PLAN_CODE=#{req.weekPlanCode}


    and PLAN_NAME=#{req.planName}


    and LINE_NO=#{req.lineNo}


    and SUBJECT_CODE=#{req.subjectCode}


    and TRIAL_STATUS IN (${req.trialStatus})


    and WORK_FLOW_INST_ID=#{req.workFlowInstId}


    and (FIRST_BEGIN_TIME=#{req.firstBeginTime} or FIRST_BEGIN_TIME=#{req.firstBeginTime1})


    and WORKER_GROUP_CODE=#{req.workerGroupCode}

ORDER BY WEEK_PLAN_CODE desc

mapper.update:
UPDATE T_WEEKLY_PLAN SET
DELETE_FLAG='1', REC_DELETOR=#{userId}, REC_DELETE_TIME=#{time}
WHERE REC_ID=#{id}

UPDATE T_OVERHAUL_PLAN SET
DELETE_FLAG='1', REC_DELETOR=#{userId}, REC_DELETE_TIME=#{time}
WHERE WEEK_PLAN_CODE=#{weekPlanCode}

UPDATE T_OVERHAUL_OBJECT SET
DELETE_FLAG='1', REC_DELETOR=#{userId}, REC_DELETE_TIME=#{time}
WHERE PLAN_CODE IN (SELECT T11.PLAN_CODE FROM T_OVERHAUL_PLAN T11 WHERE T11.WEEK_PLAN_CODE = #{weekPlanCode})

mapper.select:

FROM T_WEEKLY_PLAN WHERE 1=1 and DELETE_FLAG='0'

    and WEEK_PLAN_CODE=#{weekPlanCode}


    and PLAN_NAME=#{planName}


    and LINE_NO=#{lineNo}


    and SUBJECT_CODE=#{subjectCode}


    and TRIAL_STATUS IN (${trialStatus})


    and WORK_FLOW_INST_ID=#{workFlowInstId}


    and (FIRST_BEGIN_TIME=#{firstBeginTime} or FIRST_BEGIN_TIME=#{firstBeginTime1})


    and WORKER_GROUP_CODE=#{workerGroupCode}

ORDER BY WEEK_PLAN_CODE desc

SELECT REC_ID as "recId", WEEK_PLAN_CODE as "weekPlanCode", PLAN_NAME as "planName", LINE_NO as "lineNo", LINE_NAME as "lineName", POSITION1_NAME as "position1Name", POSITION1_CODE as "position1Code", SUBJECT_CODE as "subjectCode", (SELECT t1.NODE_NAME FROM T_EQUIPMENT_CATEGORY t1 WHERE t1.NODE_CODE=SUBJECT_CODE AND t1.DELETE_FLAG='0' AND t1.REC_STATUS='10') as "subjectName", WORKER_GROUP_CODE as "workerGroupCode", WORK_GROUP_NAME as "workGroupName", WORKER_NAME as "workerName", WORKER_CODE as "workerCode", FIRST_BEGIN_TIME as "firstBeginTime", PLAN_FINISH_TIME as "planFinishTime", PLAN_STATUS as "planStatus", TRIAL_STATUS as "trialStatus", WORK_FLOW_INST_ID as "workFlowInstId", WORK_FLOW_INST_STATUS as "workFlowInstStatus", COMPANY_CODE as "companyCode", COMPANY_NAME as "companyName", REC_CREATOR as "recCreator", REC_CREATOR_NAME as "recCreatorName", REC_CREATE_TIME as "recCreateTime", REC_REVISOR as "recRevisor", REC_REVISE_TIME as "recReviseTime", REC_DELETOR as "recDeletor", REC_DELETE_TIME as "recDeleteTime", DELETE_FLAG as "deleteFlag", ARCHIVE_FLAG as "archiveFlag", REMARK as "remark", EXT1 as "ext1", EXT2 as "ext2", EXT3 as "ext3", EXT4 as "ext4", EXT5 as "ext5" FROM T_WEEKLY_PLAN WHERE DELETE_FLAG='0' and REC_ID IN (
#{id}
) ORDER BY WEEK_PLAN_CODE desc
