mapper.update:
update
T_SEC_RISK
set
DELETE_FLAG='1', REC_DELETOR=#{userId}, REC_DELETE_TIME=#{time}
WHERE REC_ID in

    #{id}

mapper.select:
SELECT

FROM T_SEC_RISK


        and SEC_RISK_ID = #{secRiskId}


        and TO_DATE(INSPECT_DATE,'YYYY-MM-DD') >= TO_DATE(#{inspectDateStart},'YYYY-MM-DD')


        and TO_DATE(INSPECT_DATE,'YYYY-MM-DD') <= TO_DATE(#{inspectDateEnd},'YYYY-MM-DD')


        and IS_RESTORED = #{isRestoredCode}


        and REC_STATUS = #{recStatus}

    and DELETE_FLAG='0'

order By SEC_RISK_ID desc

SELECT

FROM T_SEC_RISK


        and secRiskId = #{secRiskId}


        and INSPECT_DATE = #{inspectDate}


        and RESTORE_DESC = #{restoreDesc}


        and WORK_FLOW_INST_STATUS = #{workFlowInstStatus}


        and RESTORE_DESC = #{restoreDesc}


order By SEC_RISK_ID desc
