mapper.update:
update T_EQUIPMENT set
TOTAL_MILES=#{totalMiles},
TOTAL_TRACTION_ENERGY = #{totalTractionEnergy},
TOTAL_AUXILIARY_ENERGY = #{totalAuxiliaryEnergy},
TOTAL_REGENERATED_ELECTRICITY = #{totalRegenratedElectricity}
where DELETE_FLAG='0'

    AND REC_ID=#{recId}


    AND equipCode=#{equipCode}

mapper.select:
SELECT tm.REC_ID as "recId", tm.EQUIP_CODE as "equipCode", tm.EQUIP_NAME as "equipName", tm.TOTAL_MILES as "totalMiles", tm.MILES_INCREMENT as "milesIncrement", tm.FILLIN_TIME as "fillinTime", tm.FILLIN_USER_ID as "fillinUserId", u.NAME as "fillinUserName", tm.REMARK as "remark", tm.REC_CREATOR as "recCreator", tm.REC_CREATE_TIME as "recCreateTime", tm.REC_REVISOR as "recRevisor", tm.REC_REVISE_TIME as "recReviseTime", tm.REC_DELETOR as "recDeletor", tm.REC_DELETE_TIME as "recDeleteTime", tm.TOTAL_TRACTION_ENERGY as "totalTractionEnergy", tm.TOTAL_AUXILIARY_ENERGY as "totalAuxiliaryEnergy", tm.TOTAL_REGENERATED_ELECTRICITY as "totalRegenratedElectricity", tm.TRACTION_INCREMENT as "tractionIncrement", tm.AUXILIARY_INCREMENT as "auxiliaryIncrement", tm.REGENERATED_INCREMENT as "regenratedIncrement", tm.DELETE_FLAG as "deleteFlag", tm.ARCHIVE_FLAG as "archiveFlag", tm.REC_STATUS as "recStatus", tm.EXT1 as "ext1", tm.EXT2 as "ext2", tm.EXT3 as "ext3", tm.EXT4 as "ext4", tm.EXT5 as "ext5" FROM T_TRAIN_MILEAGE tm left join SYS_USER u on u.ID=FILLIN_USER_ID WHERE 1=1
and tm.EQUIP_CODE=#{equipCode}
and SUBSTR(tm.FILLIN_TIME,1,10)>=#{startTime}
and #{endTime}>=SUBSTR(tm.FILLIN_TIME,1,10)
order by tm.FILLIN_TIME desc

SELECT tm.REC_ID as "recId", tm.EQUIP_CODE as "equipCode", tm.EQUIP_NAME as "equipName", tm.TOTAL_MILES as "totalMiles", tm.MILES_INCREMENT as "milesIncrement", tm.FILLIN_TIME as "fillinTime", tm.FILLIN_USER_ID as "fillinUserId", u.NAME as "fillinUserName", tm.REMARK as "remark", tm.REC_CREATOR as "recCreator", tm.REC_CREATE_TIME as "recCreateTime", tm.REC_REVISOR as "recRevisor", tm.REC_REVISE_TIME as "recReviseTime", tm.REC_DELETOR as "recDeletor", tm.REC_DELETE_TIME as "recDeleteTime", tm.TOTAL_TRACTION_ENERGY as "totalTractionEnergy", tm.TOTAL_AUXILIARY_ENERGY as "totalAuxiliaryEnergy", tm.TOTAL_REGENERATED_ELECTRICITY as "totalRegenratedElectricity", tm.TRACTION_INCREMENT as "tractionIncrement", tm.AUXILIARY_INCREMENT as "auxiliaryIncrement", tm.REGENERATED_INCREMENT as "regenratedIncrement", tm.DELETE_FLAG as "deleteFlag", tm.ARCHIVE_FLAG as "archiveFlag", tm.REC_STATUS as "recStatus", tm.EXT1 as "ext1", tm.EXT2 as "ext2", tm.EXT3 as "ext3", tm.EXT4 as "ext4", tm.EXT5 as "ext5" FROM T_TRAIN_MILEAGE tm left join SYS_USER u on u.ID=FILLIN_USER_ID WHERE 1=1
and tm.EQUIP_CODE=#{equipCode}
and SUBSTR(tm.FILLIN_TIME,1,10)>=#{startTime}
and #{endTime}>=SUBSTR(tm.FILLIN_TIME,1,10)
order by tm.FILLIN_TIME desc

SELECT REC_ID, EQUIP_CODE, EQUIP_NAME, "DAY", DAILY_WORK_MILE, TOTAL_WORK_MILE, DAILY_MILE, TOTAL_MILE, TRACTION_INCREMENT, TOTAL_TRACTION_ENERGY, AUXILIARY_INCREMENT, TOTAL_AUXILIARY_ENERGY, REGENRATED_INCREMENT, TOTAL_REGENRATED_ELECTRICITY, REC_CREATOR, REC_CREATE_TIME, REC_REVISOR, REC_REVISE_TIME, REC_DELETOR, REC_DELETE_TIME, DELETE_FLAG, REMARK FROM T_TARIN_MILE WHERE DELETE_FLAG='0'
AND "DAY"=#{day}
AND EQUIP_CODE=#{equipCode}
order by "DAY" desc

