mapper.select:
SELECT wd.REC_ID, wd.RULE_CODE, wd.RULE_DETAL_NAME, wd.START_DATE, wd.END_DATE, wd.PERIOD, wd.RULE_SORT, wd.REMARK, u.NAME AS REC_CREATOR, wd.REC_CREATE_TIME, wd.EXT1, wd.BEFORE_TIME FROM T_WO_RULE_DETAIL wd LEFT JOIN SYS_USER u ON wd.REC_CREATOR=u.ID where wd.DELETE_FLAG='0'
and wd.RULE_CODE=#{ruleCode}
ORDER BY wd.RULE_SORT ASC

select max(wr.RULE_CODE)
from T_WO_RULE wr
where wr.RULE_USEAGE=#{ruleUseage}

mapper.update:
UPDATE T_WO_RULE SET
RULE_CODE=#{newCode}
WHERE RULE_CODE=#{oldCode}

UPDATE T_WO_RULE SET
DELETE_FLAG='1', REC_DELETOR=#{userId}, REC_DELETE_TIME=#{time}
WHERE REC_ID IN (

    #{id}

)

UPDATE T_WO_RULE_DETAIL SET
DELETE_FLAG='1', REC_DELETOR=#{userId}, REC_DELETE_TIME=#{time}
WHERE REC_ID IN (

    #{id}

)

UPDATE T_WO_RULE_DETAIL SET
DELETE_FLAG='1', REC_DELETOR=#{userId}, REC_DELETE_TIME=#{time}
WHERE RULE_CODE IN (SELECT RULE_CODE FROM T_WO_RULE WHERE REC_ID IN (

    #{id}

))

mapper.select:
SELECT wd.REC_ID, wd.RULE_CODE, wd.RULE_DETAL_NAME, wd.START_DATE, wd.END_DATE, wd.PERIOD, wd.RULE_SORT, wd.REMARK, u.NAME AS REC_CREATOR, wd.REC_CREATE_TIME, wd.EXT1, wd.BEFORE_TIME FROM T_WO_RULE_DETAIL wd LEFT JOIN SYS_USER u ON wd.REC_CREATOR=u.ID where wd.DELETE_FLAG='0'
and wd.RULE_CODE=#{ruleCode}
ORDER BY wd.RULE_SORT ASC
