mapper.select:
SELECT dc.REC_ID as "recId", dc.COMPANY_CODE as "companyCode", dc.COMPANY_NAME as "companyName", dc.NODE_CODE as "nodeCode", dc.NODE_NAME as "nodeName", dc.PARENT_NODE_REC_ID as "parentNodeRecId", dc.NODE_LEVEL as "nodeLevel", dc.REMARK as "remark", u.NAME as "recCreator", dc.REC_CREATE_TIME as "recCreateTime", dc.REC_STATUS as "recStatus", dc.EXT1 as "ext1", dc.EXT2 as "ext2", dc.EXT3 as "ext3", dc.EXT4 as "ext4", dc.EXT5 as "ext5" FROM T_EQUIPMENT_CATEGORY dc LEFT JOIN SYS_USER u ON dc.REC_CREATOR=u.ID where dc.DELETE_FLAG='0' AND dc.REC_ID IN (
#{id}
) ORDER BY dc.NODE_CODE ASC

SELECT
NODE_CODE as "nodeCode",
NODE_NAME as "nodeName"
FROM T_EQUIPMENT_CATEGORY where 1=1

    and NODE_CODE = #{nodeCode}


    and NODE_NAME = #{nodeName}


    and NODE_LEVEL = #{nodeLevel}
