mapper.select:
SELECT om.REC_ID, om.ORG_CODE, om.ORG_NAME, om.MAJOR_CODE, om.REC_STATUS, om.REMARK, u.NAME AS REC_CREATOR, om.REC_CREATE_TIME FROM SYS_ORG_MAJOR om LEFT JOIN SYS_USER u ON om.REC_CREATOR=u.ID where om.DELETE_FLAG='0' AND om.REC_ID IN (
#{id}
) ORDER BY om.REC_CREATE_TIME ASC

SELECT T05.ORG_CODE AS orgCode, T05.ORG_NAME AS orgName, T05.MAJOR_CODE AS majorCode, T06.LINE_CODE AS lineCode FROM SYS_ORG_MAJOR T05 LEFT JOIN SYS_ORG_LINE T06 ON T05.ORG_CODE=T06.ORG_CODE LEFT JOIN SYS_ORG_TYPE T07 ON T05.ORG_CODE=T07.ORG_CODE WHERE 1=1
AND T05.MAJOR_CODE=#{subjectCode}
AND T06.LINE_CODE=#{line}
AND T07.ORG_TYPE=#{orgType}

SELECT Distinct d5.ORG_CODE, d5.ORG_NAME
FROM SYS_ORG_MAJOR d5,
     SYS_ORG_LINE d6,
     SYS_ORG_TYPE d7
where d5.DELETE_FLAG='0' AND d6.DELETE_FLAG='0' AND d7.DELETE_FLAG='0' AND d5.ORG_CODE=d6.ORG_CODE
  AND d6.ORG_CODE=d7.ORG_CODE
  AND d5.MAJOR_CODE=#{majorCode}
  AND d6.LINE_CODE=#{lineCode}
  AND d7.ORG_TYPE=#{organType}
order by d5.ORG_CODE asc

SELECT t1.*
FROM SYS_OFFICE so
         LEFT JOIN SYS_ORG_MAJOR t1 on t1.ORG_CODE=so.AREA_ID
         LEFT JOIN SYS_ORG_REGION t2 on t2.ORG_CODE=so.AREA_ID
where t1.MAJOR_CODE=#{majorCode}
  and t2.REGION_CODE=#{station}
