mapper.update:
update T_BPMN_EXAMINE set
ROLE_NAME=#{roleName}
where ROLE_ID=#{roleCode}

mapper.select:
SELECT t1.USER_ID as id, t2.NAME as userName FROM SYS_USER_ROLE t1 LEFT JOIN SYS_USER t2 ON t1.USER_ID=t2.LOGIN_NAME LEFT JOIN SYS_ROLE t3 ON t1.ROLE_ID=t3.ID WHERE 1=1
AND t1.ROLE_ID=#{roleId}
AND t3.ROLE_CODE=#{roleCode}

SELECT DISTINCT t1.USER_ID, t2.NAME AS USER_NAME, t2.OFFICE_ID FROM SYS_USER_ROLE t1 LEFT JOIN SYS_USER t2 on t1.USER_ID=t2.LOGIN_NAME LEFT JOIN SYS_ROLE t3 ON t1.ROLE_ID=t3.ID WHERE 1=1
AND t3.ROLE_CODE=#{roleCode}
AND t2.OFFICE_ID=#{orgId}

SELECT DISTINCT t1.USER_ID, t2.NAME AS USER_NAME, t2.OFFICE_ID FROM SYS_USER_ROLE t1 LEFT JOIN SYS_USER t2 on t1.USER_ID=t2.LOGIN_NAME LEFT JOIN SYS_ROLE t3 ON t1.ROLE_ID=t3.ID WHERE 1=1
AND t3.ROLE_CODE=#{roleCode}
AND t1.USER_ID IN (SELECT DISTINCT USER_CODE FROM RPLAT60.V_CO_ORG_USER T01 WHERE 1 = 1
EXISTS (SELECT 1 FROM SYS_ORG_LINE T06 WHERE T06.ORG_CODE=T01.ORG_CODE AND T06.LINE_CODE=#{lineCode})
EXISTS (SELECT 1 FROM SYS_ORG_MAJOR T05 WHERE T05.ORG_CODE=T01.ORG_CODE AND T05.MAJOR_CODE=#{majorCode})
)

select * from T_BPMN_EXAMINE
WHERE 1=1

    AND NODE_ID=#{nodeId}


    AND FLOW_ID=#{flowId}

select * from T_BPMN_EXAMINE
WHERE 1=1

    AND NODE_ID=#{nodeId}


    AND FLOW_ID=#{flowId}


    AND STEP=#{step}


    AND LINE=#{line}


    AND PARENT_ID=#{parentId}
