mapper.select:
SELECT b.REC_ID as "recId", a.REG_ORG as "regOrg", a.REG_NO as "regNo", b.EQUIP_CODE as "equipCode", a.SPECIAL_EQUIP_TYPE as "specialEquipType", et.DETECTION_PERIOD as "detectionPeriod", a.SPECIAL_EQUIP_CODE as "specialEquipCode", a.FACT_NO as "factNo", a.EQUIP_INNER_NO as "equipInnerNo", a.EQUIP_POSITION as "equipPosition", a.EQUIP_DETAILED_POSITION as "equipDetailedPosition", a.EQUIP_PARAMETER as "equipParameter", a.MANAGE_ORG as "manageOrg", a.SEC_ORG as "secOrg", a.SEC_STAFF_NAME as "secStaffName", a.SEC_STAFF_PHONE as "secStaffPhone", a.SEC_STAFF_MOBILE as "secStaffMobile", a.LINKMAN_NAME as "linkmanName", a.LINKMAN_PHONE as "linkmanPhone", a.LINKMAN_MOBILE as "linkmanMobile", a.REMARK as "remark", a.REC_CREATOR as "recCreator", a.REC_CREATE_TIME as "recCreateTime", a.REC_REVISOR as "recRevisor", a.REC_REVISE_TIME as "recReviseTime", a.REC_DELETOR as "recDeletor", a.REC_DELETE_TIME as "recDeleteTime", a.DELETE_FLAG as "deleteFlag", a.ARCHIVE_FLAG as "archiveFlag", a.REC_STATUS as "recStatus", a.EXT1 as "ext1", a.EXT2 as "ext2", a.EXT3 as "ext3", a.EXT4 as "ext4", a.EXT5 as "ext5", b.EQUIP_NAME as "equipName", b.USE_LINE_NO as "useLineNo", b.POSITION1_CODE as "position1Code", b.EQUIP_STATUS as "equipStatus", b.SPECIAL_EQUIP_FLAG, b.APPROVAL_STATUS, b.POSITION1_NAME as "position1Name", b.VERIFY_DATE as "verifyDate", b.VERIFY_VALIDITY_DATE as "verifyValidityDate", c.NAME as "manageOrgName", d.NAME as "secOrgName" FROM T_EQUIPMENT b left join T_SPECIAL_EQUIP_EXT a on a.EQUIP_CODE=b.EQUIP_CODE left join T_SPECIAL_EQUIP_TYPE et on et.TYPE_CODE=a.SPECIAL_EQUIP_TYPE left join SYS_OFFICE c on c.ID=a.MANAGE_ORG left join SYS_OFFICE d on d.AREA_ID=a.SEC_ORG where b.SPECIAL_EQUIP_FLAG=to_char(20) and b.APPROVAL_STATUS=to_char(30)
and b.EQUIP_CODE like '%' || #{equipCode} || '%'
and b.EQUIP_NAME like '%' || #{equipName} || '%'
and a.SPECIAL_EQUIP_CODE like '%' || #{specialEquipCode} || '%'
and a.FACT_NO like '%' || #{factNo} || '%'
and b.USE_LINE_NO=#{useLineNo}
and b.POSITION1_CODE=#{position1Code}
and a.SPECIAL_EQUIP_TYPE like '%' || #{specialEquipType} || '%'
and b.EQUIP_STATUS=#{equipStatus}
ORDER BY CASE WHEN b.VERIFY_VALIDITY_DATE =' ' THEN null ELSE b.VERIFY_VALIDITY_DATE END ASC nulls last, a.EQUIP_CODE asc

SELECT a.REC_ID as "recId", a.REG_ORG as "regOrg", a.REG_NO as "regNo", b.EQUIP_CODE as "equipCode", a.SPECIAL_EQUIP_TYPE as "specialEquipType", a.SPECIAL_EQUIP_CODE as "specialEquipCode", a.FACT_NO as "factNo", a.EQUIP_INNER_NO as "equipInnerNo", a.EQUIP_POSITION as "equipPosition", a.EQUIP_DETAILED_POSITION as "equipDetailedPosition", a.EQUIP_PARAMETER as "equipParameter", a.MANAGE_ORG as "manageOrg", a.SEC_ORG as "secOrg", a.SEC_STAFF_NAME as "secStaffName", a.SEC_STAFF_PHONE as "secStaffPhone", a.SEC_STAFF_MOBILE as "secStaffMobile", a.LINKMAN_NAME as "linkmanName", a.LINKMAN_PHONE as "linkmanPhone", a.LINKMAN_MOBILE as "linkmanMobile", a.REMARK as "remark", a.REC_CREATOR as "recCreator", a.REC_CREATE_TIME as "recCreateTime", a.REC_REVISOR as "recRevisor", a.REC_REVISE_TIME as "recReviseTime", a.REC_DELETOR as "recDeletor", a.REC_DELETE_TIME as "recDeleteTime", a.DELETE_FLAG as "deleteFlag", a.ARCHIVE_FLAG as "archiveFlag", a.REC_STATUS as "recStatus", a.EXT1 as "ext1", a.EXT2 as "ext2", a.EXT3 as "ext3", a.EXT4 as "ext4", a.EXT5 as "ext5", b.EQUIP_NAME as "equipName", b.USE_LINE_NO as "useLineNo", b.POSITION1_CODE as "position1Code", b.EQUIP_STATUS as "equipStatus", b.SPECIAL_EQUIP_FLAG, b.APPROVAL_STATUS, b.POSITION1_NAME as "position1Name", b.VERIFY_DATE as "verifyDate", b.VERIFY_VALIDITY_DATE as "verifyValidityDate", c.NAME as "manageOrgName", d.NAME as "secOrgName" FROM T_EQUIPMENT b right join T_SPECIAL_EQUIP_EXT a on a.EQUIP_CODE=b.EQUIP_CODE left join SYS_OFFICE c on c.ID=a.MANAGE_ORG left join SYS_OFFICE d on d.AREA_ID=a.SEC_ORG where 1=1
and b.REC_ID=#{id}
and b.EQUIP_CODE=#{equipCode}
and b.SPECIAL_EQUIP_FLAG=to_char(20) and b.APPROVAL_STATUS=to_char(30)

mapper.update:
UPDATE T_EQUIPMENT SET
VERIFY_DATE=#{verifyDate},
VERIFY_VALIDITY_DATE=#{verifyValidityDate}
WHERE EQUIP_CODE=#{equipCode}

mapper.select:
SELECT a.REC_ID as "recId", a.REG_ORG as "regOrg", a.REG_NO as "regNo", b.EQUIP_CODE as "equipCode", a.SPECIAL_EQUIP_TYPE as "specialEquipType", a.SPECIAL_EQUIP_CODE as "specialEquipCode", a.FACT_NO as "factNo", a.EQUIP_INNER_NO as "equipInnerNo", a.EQUIP_POSITION as "equipPosition", a.EQUIP_DETAILED_POSITION as "equipDetailedPosition", a.EQUIP_PARAMETER as "equipParameter", a.MANAGE_ORG as "manageOrg", a.SEC_ORG as "secOrg", a.SEC_STAFF_NAME as "secStaffName", a.SEC_STAFF_PHONE as "secStaffPhone", a.SEC_STAFF_MOBILE as "secStaffMobile", a.LINKMAN_NAME as "linkmanName", a.LINKMAN_PHONE as "linkmanPhone", a.LINKMAN_MOBILE as "linkmanMobile", a.REMARK as "remark", a.REC_CREATOR as "recCreator", a.REC_CREATE_TIME as "recCreateTime", a.REC_REVISOR as "recRevisor", a.REC_REVISE_TIME as "recReviseTime", a.REC_DELETOR as "recDeletor", a.REC_DELETE_TIME as "recDeleteTime", a.DELETE_FLAG as "deleteFlag", a.ARCHIVE_FLAG as "archiveFlag", a.REC_STATUS as "recStatus", a.EXT1 as "ext1", a.EXT2 as "ext2", a.EXT3 as "ext3", a.EXT4 as "ext4", a.EXT5 as "ext5", b.EQUIP_NAME as "equipName", b.USE_LINE_NO as "useLineNo", b.POSITION1_CODE as "position1Code", b.EQUIP_STATUS as "equipStatus", b.SPECIAL_EQUIP_FLAG, b.APPROVAL_STATUS, b.POSITION1_NAME as "position1Name", b.VERIFY_DATE as "verifyDate", b.VERIFY_VALIDITY_DATE as "verifyValidityDate", c.NAME as "manageOrgName", d.NAME as "secOrgName" FROM T_EQUIPMENT b right join T_SPECIAL_EQUIP_EXT a on a.EQUIP_CODE=b.EQUIP_CODE left join SYS_OFFICE c on c.ID=a.MANAGE_ORG left join SYS_OFFICE d on d.AREA_ID=a.SEC_ORG where 1=1 and b.SPECIAL_EQUIP_FLAG=to_char(20) and b.APPROVAL_STATUS=to_char(30) AND a.REC_ID IN (
#{id}
) ORDER BY CASE WHEN b.VERIFY_VALIDITY_DATE =' ' THEN null ELSE b.VERIFY_VALIDITY_DATE END ASC nulls last, a.EQUIP_CODE asc

SELECT a.REC_ID as "recId", a.TEST_REC_ID as "testRecId", a.TESTLIST_DETAIL_REC_ID as "testlistDetailRecId", a.EQUIP_CODE as "equipCode", a.EQUIP_NAME as "equipName", a.MAT_SPECIFI as "matSpecifi", a.MANUFACTURE_NO as "manufactureNo", a.MANUFACTURE as "manufacture", a.INSTALLATION_UNIT as "installationUnit", a.VERIFY_DATE as "verifyDate", a.VERIFICATION_NO as "verificationNo", a.VERIFICATION_TYPE as "verificationType", a.VERIFY_PROOF as "verifyProof", a.VERIFY_REPORT_NO as "verifyReportNo", a.VERIFY_REPORT_TYPE as "verifyReportType", a.VERIFY_REPORT_NAME as "verifyReportName", a.VERIFY_REPORT_DATE as "verifyReportDate", a.VERIFY_FEE as "verifyFee", a.VERIFY_RESULT as "verifyResult", a.VERIFY_CONCLUSION as "verifyConclusion", a.VERIFY_REPORT_ATT as "verifyReportAtt", a.LAST_VERIFY_DATE as "lastVerifyDate", a.NEXT_VERIFY_DATE as "nextVerifyDate", a.POSITION1_CODE as "position1Code", a.POSITION1_NAME as "position1Name", a.POSITION2_CODE as "position2Code", a.POSITION2_NAME as "position2Name", a.POSITION3 as "position3", a.POSITION_REMARK as "positionRemark", a.FAULT_NO as "faultNo", a.VERIFY_NOTE as "verifyNote", a.COMPANY_CODE as "companyCode", a.COMPANY_NAME as "companyName", a.REC_CREATOR as "recCreator", a.REC_CREATE_TIME as "recCreateTime", a.REC_REVISOR as "recRevisor", a.REC_REVISE_TIME as "recReviseTime", a.REC_DELETOR as "recDeletor", a.REC_DELETE_TIME as "recDeleteTime", a.DELETE_FLAG as "deleteFlag", a.ARCHIVE_FLAG as "archiveFlag", b.REC_STATUS as "recStatus", a.EXT1 as "ext1", a.EXT2 as "ext2", a.EXT3 as "ext3", a.EXT4 as "ext4", a.EXT5 as "ext5", b.SEND_VERIFY_NO as "sendVerifyNo", b.CHECK_NO as "checkNo", b.VERIFY_DEPT as "verifyDept", a.VERIFY_VALIDITY_DATE as "verifyValidityDate", b.EDIT_DEPT_CODE as "editDeptCode",
a.DOC_ID as "docId"
FROM T_DETECTION_DETAIL a LEFT JOIN T_DETECTION b ON a.TEST_REC_ID=b.REC_ID LEFT JOIN T_EQUIPMENT c ON c.EQUIP_CODE=a.EQUIP_CODE WHERE 1=1 and a.REC_ID=#{id}
