mapper.select:
SELECT a.REC_ID as "recId", a.EQUIP_CODE as "equipCode", b.EQUIP_NAME as "equipName", b.MAJOR_NAME as "majorName", b.SYSTEM_NAME as "systemName", b.EQUIP_TYPE_NAME as "equipTypeName", b.POSITION1_NAME as "position1Name", a.CHARGE_DATE as "chargeDate", a.CHARGE_DURATION as "chargeDuration", a.REMARK as "remark", a.REC_CREATOR as "recCreator", a.REC_CREATE_TIME as "recCreateTime", a.REC_REVISOR as "recRevisor", a.REC_REVISE_TIME as "recReviseTime", a.REC_DELETOR as "recDeletor", a.REC_DELETE_TIME as "recDeleteTime", a.DELETE_FLAG as "deleteFlag", a.ARCHIVE_FLAG as "archiveFlag", a.REC_STATUS as "recStatus", a.EXT1 as "ext1", a.EXT2 as "ext2", a.EXT3 as "ext3", a.EXT4 as "ext4", a.EXT5 as "ext5" FROM T_EQUIPMENT_CHARGE a , T_EQUIPMENT b WHERE 1=1 AND a.DELETE_FLAG='0' and a.EQUIP_CODE = b.EQUIP_CODE
and a.EQUIP_CODE like '%' || #{equipCode} || '%'
and b.EQUIP_NAME like '%' || #{equipName} || '%'
and a.CHARGE_DATE=#{chargeDate}
and b.POSITION1_CODE=#{position1Code}
and b.MAJOR_CODE=#{subjectCode}
and b.MAJOR_CODE IN (
#{major}
)
and b.SYSTEM_CODE=#{systemCode}
and b.EQUIP_TYPE_CODE=#{equipTypeCode}
ORDER BY a.CHARGE_DATE desc, a.EQUIP_CODE asc

SELECT EQUIP_NAME
FROM T_EQUIPMENT
WHERE EQUIP_CODE=#{equipCode}

