mapper.select:
SELECT REC_ID as "recId", FAULT_WORK_NO as "faultWorkNo", EQUIP_CODE as "equipCode", EQUIP_NAME as "equipName", ORG_TYPE as "orgType", OPERATOR as "operator", REPLACEMENT_NO as "replacementNo", REPLACEMENT_NAME as "replacementName", REPLACE_REASON as "replaceReason", OLD_REP_NO as "oldRepNo", NEW_REP_NO as "newRepNo", OPERATE_COST_TIME as "operateCostTime", REPLACE_DATE as "replaceDate", REMARK as "remark", DOC_ID as "docId", CAR_NO as "carNo", REC_CREATOR as "recCreator", REC_CREATE_TIME as "recCreateTime", REC_REVISOR as "recRevisor", REC_REVISE_TIME as "recReviseTime", REC_DELETOR as "recDeletor", REC_DELETE_TIME as "recDeleteTime", DELETE_FLAG as "deleteFlag", ARCHIVE_FLAG as "archiveFlag", REC_STATUS as "recStatus", EXT1 as "ext1", EXT2 as "ext2", EXT3 as "ext3", EXT4 as "ext4", EXT5 as "ext5", EXT6 as "ext6", EXT7 as "ext7" FROM T_PART_REPLACEMENT WHERE 1=1 and DELETE_FLAG='0' AND REC_ID IN (
#{id}
) ORDER BY REPLACE_DATE desc, REC_CREATE_TIME desc
