mapper.update:
UPDATE T_FAULT_DETAIL
SET

     COMPANY_CODE = #{companyCode}


    , COMPANY_NAME = #{companyName}


    , FAULT_ANALYSIS_NO = #{faultAnalysisNo}


    , FAULT_WORK_NO = #{faultWorkNo}


    , MAIN_EQUIP_CODE = #{mainEquipCode}


    , MAIN_EQUIP_NAME = #{mainEquipName}


    , MAJOR_CODE = #{majorCode}


    , RECOVERY_TIME = #{recoveryTime}


    , AFFECT_CODES = #{affectCodes}


    , FREQUENCY = #{frequency}


    , MANUFACTURE = #{manufacture}


    , FAULT_DETAIL = #{faultDetail}


    , FAULT_PROCESS_DETAIL = #{faultProcessDetail}


    , FAULT_REASON_DETAIL = #{faultReasonDetail}


    , PROBLEM_DESCR = #{problemDescr}


    , IMPROVE_DETAIL = #{improveDetail}


    , WORK_FLOW_INST_ID = #{workFlowInstId}


    , WORK_FLOW_INST_STATUS = #{workFlowInstStatus}


    , DOC_ID = #{docId}


    , REMARK = #{remark}


    , REC_STATUS = #{recStatus}


    , REC_CREATOR = #{recCreator}


    , REC_CREATE_TIME = #{recCreateTime}


    , REC_REVISOR = #{recRevisor}


    , REC_REVISE_TIME = #{recReviseTime}


    , REC_DELETOR = #{recDeletor}


    , REC_DELETE_TIME = #{recDeleteTime}


    , DELETE_FLAG = #{deleteFlag}


    , ARCHIVE_FLAG = #{archiveFlag}


    , EXT1 = #{ext1}


    , EXT2 = #{ext2}


    , EXT3 = #{ext3}


    , EXT4 = #{ext4}


    , EXT5 = #{ext5}

WHERE 1=1

    AND FAULT_ANALYSIS_NO = #{faultAnalysisNo}


    AND FAULT_NO = #{faultNo}


    AND FAULT_WORK_NO = #{faultWorkNo}

mapper.select:
SELECT d3.REC_ID as "recId", d3.COMPANY_CODE as "companyCode", d3.COMPANY_NAME as "companyName", d3.FAULT_ANALYSIS_NO as "faultAnalysisNo", d3.FAULT_NO as "faultNo", d3.FAULT_WORK_NO as "faultWorkNo", d3.MAIN_EQUIP_CODE as "mainEquipCode", d3.MAIN_EQUIP_NAME as "mainEquipName", d3.MAJOR_CODE as "majorCode", (SELECT db.NODE_NAME from T_EQUIPMENT_CATEGORY db where db.NODE_CODE=d3.MAJOR_CODE) as "majorName", (SELECT db.NODE_NAME from T_EQUIPMENT_CATEGORY db where db.NODE_CODE=d1.SYSTEM_CODE) as "systemName", (SELECT db.NODE_NAME from T_EQUIPMENT_CATEGORY db where db.NODE_CODE=d1.POSITION_CODE) as "positionName", d3.RECOVERY_TIME as "recoveryTime", d3.AFFECT_CODES as "affectCodes", d3.FREQUENCY as "frequency",
(SELECT dd.MANUFACTURE from T_EQUIPMENT dd where dd.EQUIP_CODE=d1.OBJECT_CODE) as "manufacture", d3.FAULT_DETAIL as "faultDetail", d3.FAULT_PROCESS_DETAIL as "faultProcessDetail", d3.FAULT_REASON_DETAIL as "faultReasonDetail", d3.PROBLEM_DESCR as "problemDescr", d3.IMPROVE_DETAIL as "improveDetail", d3.WORK_FLOW_INST_ID as "workFlowInstId", d3.WORK_FLOW_INST_STATUS as "workFlowInstStatus", d3.DOC_ID as "docId", d3.REMARK as "remark", d3.REC_STATUS as "recStatus", d3.REC_CREATOR as "recCreator", d3.REC_CREATE_TIME as "recCreateTime", d3.REC_REVISOR as "recRevisor", d3.REC_REVISE_TIME as "recReviseTime", d3.REC_DELETOR as "recDeletor", d3.REC_DELETE_TIME as "recDeleteTime", d3.DELETE_FLAG as "deleteFlag", d3.ARCHIVE_FLAG as "archiveFlag", d3.EXT1 as "ext1", d3.EXT2 as "ext2", d3.EXT3 as "ext3", d3.EXT4 as "ext4", d3.EXT5 as "ext5", d1.RESP_DEPT_CODE as "respDeptCode", d1.POSITION_CODE as "positionCode", d1.SYSTEM_CODE as "systemCode", d1.FAULT_LEVEL as "faultLevel", d1.LINE_CODE as "lineCode", d1.DISCOVERY_TIME as "discoveryTime", d1.FAULT_DISPLAY_CODE as "faultDisplayCode", d1.FAULT_DISPLAY_DETAIL as "faultDisplayDetail" FROM T_FAULT_DETAIL d3 left join T_FAULT_INFO d1 on d3.FAULT_NO = d1.FAULT_NO left join T_FAULT_DETAIL d2 on d2.FAULT_WORK_NO = d3.FAULT_WORK_NO
and d3.DELETE_FLAG ='0'
and d3.FAULT_NO =#{req.faultNo}
and d3.REC_STATUS =#{req.recStatus}
and d3.FREQUENCY =#{req.frequency}
and d3.MAJOR_CODE =#{req.majorCode}
and d1.LINE_CODE =#{req.lineCode}
and d1.POSITION_CODE =#{req.positionCode}
and TO_DATE(d1.DISCOVERY_TIME,'YYYY-MM-DD HH24:MI:SS')>=to_date(#{req.discoveryStartTime},'YYYY-MM-DD')
and TO_DATE(d1.DISCOVERY_TIME,'YYYY-MM-DD HH24:MI:SS')<=to_date(#{req.discoveryEndTime},'YYYY-MM-DD')
and d1.RESP_DEPT_CODE = #{req.respDeptCode}
and d3.AFFECT_CODES =#{req.affectCodes}

SELECT d3.REC_ID as "recId", d3.COMPANY_CODE as "companyCode", d3.COMPANY_NAME as "companyName", d3.FAULT_ANALYSIS_NO as "faultAnalysisNo", d3.FAULT_NO as "faultNo", d3.FAULT_WORK_NO as "faultWorkNo", d3.MAIN_EQUIP_CODE as "mainEquipCode", d3.MAIN_EQUIP_NAME as "mainEquipName", d3.MAJOR_CODE as "majorCode", (SELECT db.NODE_NAME from T_EQUIPMENT_CATEGORY db where db.NODE_CODE=d3.MAJOR_CODE) as "majorName", (SELECT db.NODE_NAME from T_EQUIPMENT_CATEGORY db where db.NODE_CODE=d1.SYSTEM_CODE) as "systemName", (SELECT db.NODE_NAME from T_EQUIPMENT_CATEGORY db where db.NODE_CODE=d1.POSITION_CODE) as "positionName", d3.RECOVERY_TIME as "recoveryTime", d3.AFFECT_CODES as "affectCodes", d3.FREQUENCY as "frequency",
(SELECT dd.MANUFACTURE from T_EQUIPMENT dd where dd.EQUIP_CODE=d1.OBJECT_CODE) as "manufacture", d3.FAULT_DETAIL as "faultDetail", d3.FAULT_PROCESS_DETAIL as "faultProcessDetail", d3.FAULT_REASON_DETAIL as "faultReasonDetail", d3.PROBLEM_DESCR as "problemDescr", d3.IMPROVE_DETAIL as "improveDetail", d3.WORK_FLOW_INST_ID as "workFlowInstId", d3.WORK_FLOW_INST_STATUS as "workFlowInstStatus", d3.DOC_ID as "docId", d3.REMARK as "remark", d3.REC_STATUS as "recStatus", d3.REC_CREATOR as "recCreator", d3.REC_CREATE_TIME as "recCreateTime", d3.REC_REVISOR as "recRevisor", d3.REC_REVISE_TIME as "recReviseTime", d3.REC_DELETOR as "recDeletor", d3.REC_DELETE_TIME as "recDeleteTime", d3.DELETE_FLAG as "deleteFlag", d3.ARCHIVE_FLAG as "archiveFlag", d3.EXT1 as "ext1", d3.EXT2 as "ext2", d3.EXT3 as "ext3", d3.EXT4 as "ext4", d3.EXT5 as "ext5", d1.RESP_DEPT_CODE as "respDeptCode", d1.POSITION_CODE as "positionCode", d1.SYSTEM_CODE as "systemCode", d1.FAULT_LEVEL as "faultLevel", d1.LINE_CODE as "lineCode", d1.DISCOVERY_TIME as "discoveryTime", d1.FAULT_DISPLAY_CODE as "faultDisplayCode", d1.FAULT_DISPLAY_DETAIL as "faultDisplayDetail" FROM T_FAULT_DETAIL d3 left join T_FAULT_INFO d1 on d3.FAULT_NO = d1.FAULT_NO left join T_FAULT_DETAIL d2 on d2.FAULT_WORK_NO = d3.FAULT_WORK_NO
and d3.DELETE_FLAG ='0'
and d3.FAULT_NO =#{faultNo}
and d3.REC_STATUS =#{recStatus}
and d3.FREQUENCY =#{frequency}
and d3.MAJOR_CODE =#{majorCode}
and d1.LINE_CODE =#{lineCode}
and d1.POSITION_CODE =#{positionCode}
and to_date( d1.DISCOVERY_TIME , 'YYYY-MM-DD HH24:MI:SS') >= to_date(#{discoveryStartTime}, 'YYYY-MM-DD')
and TO_DATE(d1.DISCOVERY_TIME,'YYYY-MM-DD HH24:MI:SS') <= TO_DATE(#{discoveryEndTime},'YYYY-MM-DD')
and d1.RESP_DEPT_CODE = #{respDeptCode}
and d3.AFFECT_CODES =#{affectCodes}
and d3.REC_ID in (
#{id}
)

SELECT d1.FAULT_NO as "faultNo", d1.LINE_CODE as "lineCode", d1.MAJOR_CODE as "majorCode", d1.SYSTEM_CODE as "systemCode", (SELECT db.NODE_NAME from T_EQUIPMENT_CATEGORY db where db.NODE_CODE=d1.MAJOR_CODE) as "majorName", (SELECT db.NODE_NAME from T_EQUIPMENT_CATEGORY db where db.NODE_CODE=d1.SYSTEM_CODE) as "systemName", d1.FAULT_DISPLAY_CODE as "faultDisplayCode", d1.FAULT_DISPLAY_DETAIL as "faultDisplayDetail", d3.FAULT_REASON_DETAIL as "faultReasonDetail", d3.FAULT_PROCESS_DETAIL as "faultProcessDetail", d1.DISCOVERY_TIME as "discoveryTime", d1.RESP_DEPT_CODE as "respDeptCode", d1.FAULT_LEVEL as "faultLevel", d3.AFFECT_CODES as "affectCodes", d1.FAULT_STATUS as "faultStatus", d3.PROBLEM_DESCR as "problemDescr", d3.IMPROVE_DETAIL as "improveDetail", d3.REC_STATUS as "recStatus", d3.FREQUENCY as "frequency", d1.POSITION_CODE as "positionCode", (SELECT db.NODE_NAME from SYS_REGION db where db.NODE_CODE=d1.POSITION_CODE) as "positionName", d1.OBJECT_CODE as "objectCode", d1.OBJECT_NAME as "objectName", (SELECT dd.MANUFACTURE from T_EQUIPMENT dd where dd.EQUIP_CODE=d1.OBJECT_CODE) as "manufacture", d3.RECOVERY_TIME as "recoveryTime", d3.FAULT_ANALYSIS_NO as "faultAnalysisNo", d3.FAULT_WORK_NO as "faultWorkNo", d3.FAULT_DETAIL as "faultDetail", d3.DOC_ID as "docId", d3.REC_CREATOR as "recCreator" FROM T_FAULT_INFO d1 , T_FAULT_DETAIL d3 WHERE 1=1 and d1.FAULT_NO = d3.FAULT_NO
and d3.FAULT_ANALYSIS_NO =#{faultAnalysisNo}
and d3.FAULT_NO =#{faultNo}
and d3.FAULT_WORK_NO =#{faultWorkNo}
order by d1.FAULT_NO asc

SELECT USER_CODE as "userId", USER_NAME as "userName", ORG_CODE as "orgCode" FROM RPLAT60.V_CO_ORG_USER
WHERE 1=1

    AND USER_CODE IN

        #{code}



    AND ORG_CODE = #{orgCode}

SELECT PARENT_CODE as "orgCode" FROM RPLAT60.T_WW_ORG
WHERE 1=1

    AND ORG_CODE = #{orgCode}

SELECT d3.REC_ID as "recId", d3.COMPANY_CODE as "companyCode", d3.COMPANY_NAME as "companyName", d3.FAULT_ANALYSIS_NO as "faultAnalysisNo", d3.FAULT_NO as "faultNo", d3.FAULT_WORK_NO as "faultWorkNo", d3.MAIN_EQUIP_CODE as "mainEquipCode", d3.MAIN_EQUIP_NAME as "mainEquipName", d3.MAJOR_CODE as "majorCode", (SELECT db.NODE_NAME from T_EQUIPMENT_CATEGORY db where db.NODE_CODE=d3.MAJOR_CODE) as "majorName", (SELECT db.NODE_NAME from T_EQUIPMENT_CATEGORY db where db.NODE_CODE=d1.SYSTEM_CODE) as "systemName", (SELECT db.NODE_NAME from SYS_REGION db where db.NODE_CODE=d1.POSITION_CODE) as "positionName", d3.RECOVERY_TIME as "recoveryTime", d3.AFFECT_CODES as "affectCodes", d3.FREQUENCY as "frequency", (SELECT dd.MANUFACTURE from T_EQUIPMENT dd where dd.EQUIP_CODE=d1.OBJECT_CODE) as "manufacture", d3.FAULT_DETAIL as "faultDetail", d3.FAULT_PROCESS_DETAIL as "faultProcessDetail", d3.FAULT_REASON_DETAIL as "faultReasonDetail", d3.PROBLEM_DESCR as "problemDescr", d3.IMPROVE_DETAIL as "improveDetail", d3.WORK_FLOW_INST_ID as "workFlowInstId", d3.WORK_FLOW_INST_STATUS as "workFlowInstStatus", d3.DOC_ID as "docId", d3.REMARK as "remark", d3.REC_STATUS as "recStatus", d3.REC_CREATOR as "recCreator", d3.REC_CREATE_TIME as "recCreateTime", d3.REC_REVISOR as "recRevisor", d3.REC_REVISE_TIME as "recReviseTime", d3.REC_DELETOR as "recDeletor", d3.REC_DELETE_TIME as "recDeleteTime", d3.DELETE_FLAG as "deleteFlag", d3.ARCHIVE_FLAG as "archiveFlag", d3.EXT1 as "ext1", d3.EXT2 as "ext2", d3.EXT3 as "ext3", d3.EXT4 as "ext4", d3.EXT5 as "ext5", d1.RESP_DEPT_CODE as "respDeptCode", d2.FAULT_REASON_CODE as "faultReasonCode", d1.POSITION_CODE as "positionCode", d1.SYSTEM_CODE as "systemCode", d1.FAULT_LEVEL as "faultLevel", d1.LINE_CODE as "lineCode", d2.WORK_CLASS as "workClass", d1.DISCOVERY_TIME as "discoveryTime", d1.FAULT_DISPLAY_CODE as "faultDisplayCode", d1.FAULT_DISPLAY_DETAIL as "faultDisplayDetail" FROM T_FAULT_DETAIL d3, T_FAULT_INFO d1, T_FAULT_ORDER d2
and d3.FAULT_NO =#{faultNo}
and d3.FAULT_ANALYSIS_NO =#{faultAnalysisNo}
and d3.FAULT_WORK_NO =#{faultWorkNo}
and d3.FAULT_NO = d1.FAULT_NO and d1.FAULT_NO = d2.FAULT_NO and d2.FAULT_WORK_NO = d3.FAULT_WORK_NO
ORDER BY d3.FAULT_ANALYSIS_NO DESC
