mapper.select:
SELECT ff.REC_ID, ff.FOLLOW_NO, ff.FAULT_NO, ff.FAULT_WORK_NO, ff.MAJOR_CODE, ff.POSITION_CODE,
ff.FOLLOW_USER_ID, ff.FOLLOW_USER_NAME, ff.FOLLOW_TIME, ff.FOLLOW_START_DATE, ff.FOLLOW_END_DATE,
ff.FOLLOW_CYCLE, ff.FOLLOW_STATUS, ff.FOLLOW_LEADER_ID, ff.FOLLOW_LEADER_NAME, ff.FOLLOW_REASON, ff.REMARK,
ff.FOLLOW_CLOSER_ID, ff.FOLLOW_CLOSER_NAME, ff.DISPATCH_USER_ID, ff.DISPATCH_USER_NAME, ff.DISPATCH_TIME,
ff.FOLLOW_CLOSE_TIME, ff.REC_CREATOR, ff.REC_CREATE_TIME, ff.REC_DELETOR, ff.REC_DELETE_TIME, ff.DELETE_FLAG
FROM T_FAULT_FOLLOW ff
LEFT JOIN SYS_USER u ON u.LOGIN_NAME=ff.FOLLOW_LEADER_ID AND u.DEL_FLAG='0'
WHERE ff.DELETE_FLAG='0'

    AND ff.FOLLOW_NO LIKE '%' || #{followNo} || '%'


    AND ff.FAULT_WORK_NO LIKE '%' || #{faultWorkNo} || '%'


    AND ff.FOLLOW_STATUS=#{followStatus}


    AND (ff.FOLLOW_USER_ID=#{userId} OR ff.FOLLOW_LEADER_ID=#{userId}
    OR ff.FOLLOW_CLOSER_ID=#{userId} OR ff.DISPATCH_USER_ID=#{userId}

        OR u.OFFICE_ID=#{officeId}

    )

ORDER BY ff.REC_CREATE_TIME DESC

SELECT REC_ID, FOLLOW_NO, FAULT_NO, FAULT_WORK_NO, MAJOR_CODE, POSITION_CODE, FOLLOW_USER_ID, FOLLOW_USER_NAME, FOLLOW_TIME, FOLLOW_START_DATE, FOLLOW_END_DATE, FOLLOW_CYCLE, FOLLOW_STATUS, FOLLOW_LEADER_ID, FOLLOW_LEADER_NAME, FOLLOW_REASON, DISPATCH_USER_ID, DISPATCH_USER_NAME, DISPATCH_TIME, REMARK, FOLLOW_CLOSER_ID, FOLLOW_CLOSER_NAME, FOLLOW_CLOSE_TIME, REC_CREATOR, REC_CREATE_TIME, REC_DELETOR, REC_DELETE_TIME, DELETE_FLAG FROM T_FAULT_FOLLOW WHERE DELETE_FLAG='0'
AND REC_ID=#{id}
AND FOLLOW_NO=#{followNo}
ORDER BY REC_CREATE_TIME DESC

SELECT REC_ID, FOLLOW_NO, REPORT_USER_ID, REPORT_USER_NAME, REPORT_TIME, REPORT_DETAIL, DOC_ID, EXAMINE_USER_ID, EXAMINE_USER_NAME, EXAMINE_TIME, EXAMINE_OPINION, EXAMINE_STATUS FROM T_FAULT_FOLLOW_REPORT WHERE DELETE_FLAG='0' AND FOLLOW_NO=#{followNo}
AND EXAMINE_STATUS='0' AND rownum=1
ORDER BY REPORT_TIME DESC

SELECT REC_ID, FOLLOW_NO, FAULT_NO, FAULT_WORK_NO, MAJOR_CODE, POSITION_CODE, FOLLOW_USER_ID, FOLLOW_USER_NAME, FOLLOW_TIME, FOLLOW_START_DATE, FOLLOW_END_DATE, FOLLOW_CYCLE, FOLLOW_STATUS, FOLLOW_LEADER_ID, FOLLOW_LEADER_NAME, FOLLOW_REASON, REMARK, FOLLOW_CLOSER_ID, FOLLOW_CLOSER_NAME, FOLLOW_CLOSE_TIME, REC_CREATOR, REC_CREATE_TIME, REC_DELETOR, REC_DELETE_TIME, DELETE_FLAG FROM T_FAULT_FOLLOW WHERE DELETE_FLAG='0'
AND FOLLOW_NO LIKE '%' || #{followNo} || '%'
AND FAULT_WORK_NO LIKE '%' || #{faultWorkNo} || '%'
AND FOLLOW_STATUS=#{followStatus}
and REC_ID in (
#{id}
)
ORDER BY REC_CREATOR DESC
