mapper.select:
SELECT OBJECT_ID as "objectId", REC_ID as "recId", ITEM_NAME as "itemName", ORDER_CODE as "orderCode", MODEL_NAME as "modelName", OBJECT_CODE as "objectCode", OBJECT_NAME as "objectName", WORK_USER_ID as "workUserId", WORK_USER_NAME as "workUserName", ITEM_TYPE as "itemType", TRAIN_NUMBER as "trainNumber", WORK_RESULT as "workResult", INSPECT_ITEM_VALUE as "inspectItemValue", WORK_TIME as "workTime",
(SELECT COUNT(1) FROM RPLAT60.T_WW_ATTACHMENT T60 WHERE T60.BUSINESS_GUID = T_OVERHAUL_ITEM.REC_ID) as "docId",
WORK_DETAIL as "workDetail", REMARK as "remark", ERROR_FLAG as "errorFlag", ITEM_UNIT as "itemUnit", DEFAULT_VALUE as "defaultValue", MAX_VALUE as "maxValue", MIN_VALUE as "minValue", MUTUAL_INSPECTION_PEOPLE as "mutualInspectionPeople", MUTUAL_INSPECTION_PEOPLE_ID as "mutualInspectionPeopleId", SPECIAL_INSPECTION_PEOPLE as "specialInspectionPeople", SPECIAL_INSPECTION_PEOPLE_ID as "specialInspectionPeopleId", MODEL_SEQUENCE as "modelSequence", SEQUENCE_ID as "sequenceId", TDMER02_ID as "tdmer02Id", EXT1 as "ext1", EXT2 as "ext2", EXT3 as "ext3", EXT4 as "ext4", EXT5 as "ext5" FROM T_OVERHAUL_ITEM WHERE 1=1 AND OBJECT_CODE=#{req.objectCode}
and ITEM_NAME=#{req.itemName}
and ORDER_CODE=#{req.orderCode}
and OBJECT_ID=#{req.objectId}
and MODEL_NAME=#{req.modelName}
order by TO_NUMBER(NVL(TRIM(MODEL_SEQUENCE),0)),TO_NUMBER(NVL(TRIM(SEQUENCE_ID),0)),MODEL_NAME,ITEM_NAME asc

