WORK_DETAIL as "workDetail", REMARK as "remark", ERROR_FLAG as "errorFlag", ITEM_UNIT as "itemUnit", DEFAULT_VALUE as "defaultValue", MAX_VALUE as "maxValue", MIN_VALUE as "minValue", MUTUAL_INSPECTION_PEOPLE as "mutualInspectionPeople", MUTUAL_INSPECTION_PEOPLE_ID as "mutualInspectionPeopleId", SPECIAL_INSPECTION_PEOPLE as "specialInspectionPeople", SPECIAL_INSPECTION_PEOPLE_ID as "specialInspectionPeopleId", MODEL_SEQUENCE as "modelSequence", SEQUENCE_ID as "sequenceId", TDMER02_ID as "tdmer02Id", EXT1 as "ext1", EXT2 as "ext2", EXT3 as "ext3", EXT4 as "ext4", EXT5 as "ext5" FROM T_OVERHAUL_ITEM WHERE 1=1 AND OBJECT_CODE=#{objectCode}
